    jq().toastmessage('showErrorToast', "This Case has no Investigation (Enrolment) Form");
    alert("This Case has no Investigation (Enrolment) Form. You will now be directed to the patient dashboard");

    location.href = window.location.protocol + "//" + window.location.host +
        '/' + OPENMRS_CONTEXT_PATH + '/coreapps/clinicianfacing/patient.page?patientId='
        + queryParameters['patientId']; // Causes page to reload
});

