          if (jq("#invasive-ventilation").find("input[type='radio']:checked").val() != "90003") {
              jq("#worst-fio-ratio-container").hide();
              jq("#worst-spo-ratio-container").hide();
          }

          if (!jq(".complications").find('#other-complications').find(':checkbox').prop('checked')) {
