ifMode.script:
if (jQuery) {
    jq(document).ready(function () {
        jq("#other-symptoms-during-admission-container").hide();
        jq("#specify-other-complications-container").hide();
        jq("#corona-virus-result-container").hide();
        jq("#malaria-falciparum-result-container").hide();
        jq("#hiv-result-container").hide();
        jq("#other-anti-biotics-text-container").hide();
        jq("#novel-experimental-therapeutic-agent-container").hide();
        jq("#antivirals-container").hide();
        jq("#corticosteroid-container").hide();
        jq("#corticosteroid-route-container").hide();
        jq("#other-therapy-container").hide();
        jq("#interface-container").hide();
        jq("#02-flow-container").hide();

        $allRows = jq(this).find('.other-diagnostic-test');
        $allRows.hide();
        jq('#1-removeEntry').remove();
        jq('#10-addEntry').remove();
        jq('#1-toggleContainer').show();

        jq('#11-removeEntry').remove();
        jq('#20-addEntry').remove();
        jq('#11-toggleContainer').show();

        jq('.addEntry').on("click", function () {
            var correctedAddButtonId = parseFloat(this.id) + 1;
            var contentAddId = "#" + correctedAddButtonId + "-toggleContainer";
            jq(contentAddId).toggle(true);
            jq('#' + this.id).toggle(false);
            jq('#' + parseFloat(this.id) + '-removeEntry').toggle(true);
            return false;
        });

        jq('.removeEntry').on("click", function () {
            var correctedRemoveButtonId = parseFloat(this.id) - 1;
            var contentAddId = "#" + parseFloat(this.id) + "-toggleContainer";
            jq(contentAddId).toggle(false);
            jq(':input:not(:button)', contentAddId).val([]);
            jq('#' + correctedRemoveButtonId + '-addEntry').toggle(true);
            jq('#' + correctedRemoveButtonId + '-removeEntry').toggle(true);

            return false;
        });
    });
}

if (jQuery) {
    jq(document).ready(function () {
        $allRows = jq(this).find('.other-diagnostic-test');
        $allRows.hide();
        jq('#1-removeEntry').remove();
        jq('#10-addEntry').remove();
        jq('#1-toggleContainer').show();
        jq('#11-removeEntry').remove();
        jq('#20-addEntry').remove();
        jq('#11-toggleContainer').show();
        for (let x = 0; x<=10; x++) {
            if (jq(this).find('#' +x+ '-other-diagnostic-test').find("input[type$='text']").val()!='') {
                jq(this).find('#' +x+ '-toggleContainer').show();
                jq(this).find('#' +x+ '-addEntry').hide();
            }
            else {
                x-=1;
                jq(this).find('#' +x+ '-addEntry').show();
                break;
            }
        }

        jq('.addEntry').on("click", function () {
        var correctedAddButtonId = parseFloat(this.id) + 1;
        var contentAddId = "#" + correctedAddButtonId + "-toggleContainer";
        jq(contentAddId).toggle(true);
        jq('#' + this.id).toggle(false);
        jq('#' + parseFloat(this.id) + '-removeEntry').toggle(true);
        return false;
        });

        jq('.removeEntry').on("click", function () {
        var correctedRemoveButtonId = parseFloat(this.id) - 1;
        var contentAddId = "#" + parseFloat(this.id) + "-toggleContainer";
        jq(contentAddId).toggle(false);
        jq(':input:not(:button)', contentAddId).val([]);
        jq('#' + correctedRemoveButtonId + '-addEntry').toggle(true);
        jq('#' + correctedRemoveButtonId + '-removeEntry').toggle(true);

        return false;
        });

        if (!jq(".symptom").find('#other-symptom').find(':checkbox').prop('checked')) {
            jq("#other-symptoms-during-admission-container").hide();
        }

        if (!jq(".complications").find('#other-complications').find(':checkbox').prop('checked')) {
            jq("#specify-other-complications-container").hide();
        }

        if (jq("#corona-virus-test").find("input[type='radio']:checked").val() != "90003") {
            jq("#corona-virus-result-container").hide();
        }

        if (jq("#malaria-falciparum-test").find("input[type='radio']:checked").val() != "90003") {
            jq("#malaria-falciparum-result-container").hide();
        }

        if (jq("#hiv-test").find("input[type='radio']:checked").val() != "90003") {
            jq("#hiv-result-container").hide();
        }

        if (jq("#antibiotics").find("input[type='radio']:checked").val() != "90003") {
            jq("#other-anti-biotics-text-container").hide();
        }

        if (jq("#novel-experimental-therapeutic-agent").find("input[type='radio']:checked").val() != "90003") {
            jq("#novel-experimental-therapeutic-agent-container").hide();
        }

        if (jq("#antiviral").find("input[type='radio']:checked").val() != "90003") {
            jq("#antivirals-container").hide();
        }

        if (jq("#corticosteroid").find("input[type='radio']:checked").val() != "90003") {
            jq("#corticosteroid-container").hide();
            jq("#corticosteroid-route-container").hide();
        }

        if (jq("#oxygen-therapy").find("input[type='radio']:checked").val() != "90003") {
            jq("#interface-container").hide();
            jq("#02-flow-container").hide();
        }

    });
}

htmlform.script:
if (jQuery) {
    jq(document).ready(function () {
        jq(".symptom").change(function () {
            if (jq(this).find('#other-symptom').find(':checkbox').prop('checked')) {
                enable_fields("other-symptoms-during-admission");
                jq("#other-symptoms-during-admission-container").show();
            } else {
                disable_fields("other-symptoms-during-admission");
                jq("#other-symptoms-during-admission-container").hide();
            }
        });
        jq("#corona-virus-test").click(function () {
            if (jq(this).find("input[type='radio']:checked").val() == "90003") {
                enable_fields("corona-virus-result");
                jq("#corona-virus-result-container").show();
            } else {
                disable_fields("corona-virus-result");
                jq("#corona-virus-result-container").hide();
            }
        });

        jq("#malaria-falciparum-test").click(function () {
            if (jq(this).find("input[type='radio']:checked").val() == "90003") {
                enable_fields("malaria-falciparum-result");
                jq("#malaria-falciparum-result-container").show();
            } else {
                disable_fields("malaria-falciparum-result");
                jq("#malaria-falciparum-result-container").hide();
            }
        });

        jq("#hiv-test").click(function () {
            if (jq(this).find("input[type='radio']:checked").val() == "90003") {
                enable_fields("hiv-result");
                jq("#hiv-result-container").show();
            } else {
                disable_fields("hiv-result");
                jq("#hiv-result-container").hide();
            }
        });

        jq('#other-diagnostic-test').on("keyup", function() {
            if (jq(this).find("input[type='text']").val() != '') {
            enable_fields("other-diagnostic-result");
            } else {
            disable_fields("other-diagnostic-result");
            }
        });



        jq("#novel-experimental-therapeutic-agent").click(function () {
            if (jq(this).find("input[type='radio']:checked").val() == "90003") {
                enable_fields("specify-novel-experimental-therapeutic-agent");
                jq("#novel-experimental-therapeutic-agent-container").show();
            } else {
                disable_fields("specify-novel-experimental-therapeutic-agent");
                jq("#novel-experimental-therapeutic-agent-container").hide();
            }
        });
        jq("#antiviral").click(function () {
            if (jq(this).find("input[type='radio']:checked").val() == "90003") {
                enable_fields("antivirals");
                jq("#antivirals-container").show();
            } else {
                disable_fields("antivirals");
                jq("#antivirals-container").hide();
            }
        });
        jq("#corticosteroid").click(function () {
            if (jq(this).find("input[type='radio']:checked").val() == "90003") {
                enable_fields("corticosteroid-route");
                enable_fields("other-corticosteroid");
                jq("#corticosteroid-container").show();
                jq("#corticosteroid-route-container").show();
            } else {
                disable_fields("corticosteroid-route");
                disable_fields("other-corticosteroid");
                jq("#corticosteroid-container").hide();
                jq("#corticosteroid-route-container").hide();
            }
        });
        jq("#other-therapy").click(function () {
            if (jq(this).find("input[type='radio']:checked").val() == "90003") {
                enable_fields("specify-other-therapy");
                jq("#other-therapy-container").show();
            } else {
                disable_fields("specify-other-therapy");
                jq("#other-therapy-container").hide();
            }
        });
        jq("#oxygen-therapy").click(function () {
            if (jq(this).find("input[type='radio']:checked").val() == "90003") {
                enable_fields("02-flow");
                enable_fields("interface");
                jq("#interface-container").show();
                jq("#02-flow-container").show();
            } else {
                disable_fields("02-flow");
                disable_fields("interface");
                jq("#interface-container").hide();
                jq("#02-flow-container").hide();
            }
        });

        jq(".complications").change(function () {
            if (jq(this).find('#other-complications').find(':checkbox').prop('checked')) {
                enable_fields("specify-other-complications");
                jq("#specify-other-complications-container").show();
            } else {
                disable_fields("specify-other-complications");
                jq("#specify-other-complications-container").hide();
            }
        });

        jq("#antibiotics").click(function () {
            if (jq(this).find("input[type='radio']:checked").val() == "90003") {
                enable_fields("specify-other-anti-biotics-text");
                jq("#other-anti-biotics-text-container").show();
            } else {
                disable_fields("specify-other-anti-biotics-text");
                jq("#other-anti-biotics-text-container").hide();
            }
        });


        beforeSubmit.push(function () {

                if(jq('#shock').find(':checkbox').prop('checked')) {
                    var shockAnswer = jq('#shock-answer').find("input[type='radio']:checked").val();
                    if(!shockAnswer){
                        jq().toastmessage('showErrorToast', "Choose Answer to 'Shock' under Complication");
                        return false;
                    }
                }
                if(jq('#bacteria').find(':checkbox').prop('checked')) {
                    var bacteriaAnswer = jq('#bacteria-answer').find("input[type='radio']:checked").val();
                    if(!bacteriaAnswer){
                        jq().toastmessage('showErrorToast', "Choose Answer to 'Bacteria' under Complication");
                        return false;
                    }
                }
                if(jq('#seizure').find(':checkbox').prop('checked')) {
                    var seizureAnswer = jq('#seizure-answer').find("input[type='radio']:checked").val();
                    if(!seizureAnswer){
                        jq().toastmessage('showErrorToast', "Choose Answer to 'seizure' under Complication");
                        return false;
                    }
                }
                if(jq('#bleeding').find(':checkbox').prop('checked')) {
                    var bleedingAnswer = jq('#bleeding-answer').find("input[type='radio']:checked").val();
                    if(!bleedingAnswer){
                        jq().toastmessage('showErrorToast', "Choose Answer to 'bleeding' under Complication");
                        return false;
                    }
                }
                if(jq('#meningitis-encephalitis').find(':checkbox').prop('checked')) {
                    var meningitisAnswer = jq('#meningitis-encephalitis-answer').find("input[type='radio']:checked").val();
                    if(!meningitisAnswer){
                        jq().toastmessage('showErrorToast', "Choose Answer to 'meningitis/encephalitis' under Complication");
                        return false;
                    }
                }
                if(jq('#endocarditis').find(':checkbox').prop('checked')) {
                    var endocarditisAnswer = jq('#endocarditis-answer').find("input[type='radio']:checked").val();
                    if(!endocarditisAnswer){
                        jq().toastmessage('showErrorToast', "Choose Answer to 'endocarditis' under Complication");
                        return false;
                    }
                }
                if(jq('#anaemia').find(':checkbox').prop('checked')) {
                    var anaemiaAnswer = jq('#anaemia-answer').find("input[type='radio']:checked").val();
                    if(!anaemiaAnswer){
                        jq().toastmessage('showErrorToast', "Choose Answer to 'anaemia' under Complication");
                        return false;
                    }
                }
                if(jq('#myocarditis-pericarditis').find(':checkbox').prop('checked')) {
                    var myocarditisAnswer = jq('#myocarditis-pericarditis-answer').find("input[type='radio']:checked").val();
                    if(!myocarditisAnswer){
                        jq().toastmessage('showErrorToast', "Choose Answer to 'myocarditis/pericarditis' under Complication");
                        return false;
                    }
                }
                if(jq('#cardiac-arrhythmia').find(':checkbox').prop('checked')) {
                    var arrhythmiaAnswer = jq('#cardiac-arrhythmia-answer').find("input[type='radio']:checked").val();
                    if(!arrhythmiaAnswer){
                        jq().toastmessage('showErrorToast', "Choose Answer to 'cardiac arrhythmia' under Complication");
                        return false;
                    }
                }
                if(jq('#acute-renal-injury').find(':checkbox').prop('checked')) {
                    var renalAnswer = jq('#acute-renal-injury-answer').find("input[type='radio']:checked").val();
                    if(!renalAnswer){
                        jq().toastmessage('showErrorToast', "Choose Answer to 'acute renal injury' under Complication");
                        return false;
                    }
                }
                if(jq('#cardiac-arrest').find(':checkbox').prop('checked')) {
                    var arrestAnswer = jq('#cardiac-arrest-answer').find("input[type='radio']:checked").val();
                    if(!arrestAnswer){
                        jq().toastmessage('showErrorToast', "Choose Answer to 'cardiac arrest' under Complication");
                        return false;
                    }
                }
                if(jq('#pancreatitis').find(':checkbox').prop('checked')) {
                    var pancreatitisAnswer = jq('#pancreatitis-answer').find("input[type='radio']:checked").val();
                    if(!pancreatitisAnswer){
                        jq().toastmessage('showErrorToast', "Choose Answer to 'pancreatitis' under Complication");
                        return false;
                    }
                }
                if(jq('#pneumonia').find(':checkbox').prop('checked')) {
                    var pneumoniaAnswer = jq('#pneumonia-answer').find("input[type='radio']:checked").val();
                    if(!pneumoniaAnswer){
                        jq().toastmessage('showErrorToast', "Choose Answer to 'pneumonia' under Complication");
                        return false;
                    }
                }
                if(jq('#liver-dysfunction').find(':checkbox').prop('checked')) {
                    var dysfunctionAnswer = jq('#liver-dysfunction-answer').find("input[type='radio']:checked").val();
                    if(!dysfunctionAnswer){
                        jq().toastmessage('showErrorToast', "Choose Answer to 'liver dysfunction' under Complication");
                        return false;
                    }
                }
                if(jq('#bronchiolitis').find(':checkbox').prop('checked')) {
                    var bronchiolitisAnswer = jq('#bronchiolitis-answer').find("input[type='radio']:checked").val();
                    if(!bronchiolitisAnswer){
                        jq().toastmessage('showErrorToast', "Choose Answer to 'bronchiolitis' under Complication");
                        return false;
                    }
                }
                if(jq('#cardiomyopathy').find(':checkbox').prop('checked')) {
                    var cardiomyopathyAnswer = jq('#cardiomyopathy-answer').find("input[type='radio']:checked").val();
                    if(!cardiomyopathyAnswer){
                        jq().toastmessage('showErrorToast', "Choose Answer to 'cardiomyopathy' under Complication");
                        return false;
                    }
                }
                if(jq('#acute-respiratory-distress-syndrome').find(':checkbox').prop('checked')) {
                    var distressAnswer = jq('#acute-respiratory-distress-syndrome-answer').find("input[type='radio']:checked").val();
                    if(!distressAnswer){
                        jq().toastmessage('showErrorToast', "Choose Answer to 'acute respiratory distress syndrome' under Complication");
                        return false;
                    }
                }
                return true;
        })
    });
}
