ifMode.script:
if (jQuery) {
    jq(document).ready(function () {
        disable_fields("165844_1"); //test
        disable_fields("165844_2");
        disable_fields("165844_3");
        disable_fields("165844_4");
        disable_fields("165844_5");

        disable_fields("99291_1"); //results
        disable_fields("99291_2");
        disable_fields("99291_3");
        disable_fields("99291_4");
        disable_fields("99291_5");

        disable_fields("165845_1"); //lab number
        disable_fields("165845_2");
        disable_fields("165845_3");
        disable_fields("165845_4");
        disable_fields("165845_5");

        jq("#date-of-diagnosis-container").hide();
        jq("#how-was-the-diagnosis-confirmed-container").hide();
        jq("#diagnosis-type-container").hide();
        jq("#specify-other-container").hide();
        jq("#other-medical-intervention-container").hide();

        $allRows = jq(this).find('.multi-drug');
        $allRows.hide();
        jq('#1-removeEntry').remove();
        jq('#10-addEntry').remove();
        jq('#1-toggleContainer').show();

        jq('#11-removeEntry').remove();
        jq('#20-addEntry').remove();
        jq('#11-toggleContainer').show();

        jq('.addEntry').on("click", function () {
            var correctedAddButtonId = parseFloat(this.id) + 1;
            var contentAddId = "#" + correctedAddButtonId + "-toggleContainer";
            jq(contentAddId).toggle(true);
            jq('#' + this.id).toggle(false);
            jq('#' + parseFloat(this.id) + '-removeEntry').toggle(true);

            return false;
        });

        jq('.removeEntry').on("click", function () {
            var correctedRemoveButtonId = parseFloat(this.id) - 1;
            var contentAddId = "#" + parseFloat(this.id) + "-toggleContainer";
            jq(contentAddId).toggle(false);
            jq(':input:not(:button)', contentAddId).val([]);
            jq('#' + correctedRemoveButtonId + '-addEntry').toggle(true);
            jq('#' + correctedRemoveButtonId + '-removeEntry').toggle(true);

            return false;
        });

        $allRows_vitals = jq(this).find('.multi-vitals');
        $allRows_vitals.hide();
        jq('#1-removeEntry-vital').remove();
        jq('#10-addEntry-vital').remove();
        jq('#1-toggleContainer-vital').show();

        jq('#11-removeEntry-vital').remove();
        jq('#20-addEntry-vital').remove();
        jq('#11-toggleContainer-vital').show();

        jq('.addEntry-vital').on("click", function () {
            var correctedAddButtonId = parseFloat(this.id) + 1;
            var contentAddId = "#" + correctedAddButtonId + "-toggleContainer-vital";
            jq(contentAddId).toggle(true);
            jq('#' + this.id).toggle(false);
            jq('#' + parseFloat(this.id) + '-removeEntry-vital').toggle(true);

            return false;
        });

        jq('.removeEntry-vital').on("click", function () {
            var correctedRemoveButtonId = parseFloat(this.id) - 1;
            var contentAddId = "#" + parseFloat(this.id) + "-toggleContainer-vital";
            jq(contentAddId).toggle(false);
            jq(':input:not(:button)', contentAddId).val([]);
            jq('#' + correctedRemoveButtonId + '-addEntry-vital').toggle(true);
            jq('#' + correctedRemoveButtonId + '-removeEntry-vital').toggle(true);

            return false;
        });
    });
}

if (jQuery) {
    jq(document).ready(function () {
        if (jq("#covid-confirmed").find("input[type='radio']:checked").val() == "90003") {
            jq("#diagnosis-type-container").hide();
        } else if (jq("#covid-confirmed").find("input[type='radio']:checked").val() == "90004") {
            jq("#date-of-diagnosis-container").hide();
            jq("#how-was-the-diagnosis-confirmed-container").hide();
        } else {
            jq("#date-of-diagnosis-container").hide();
            jq("#how-was-the-diagnosis-confirmed-container").hide();
            jq("#diagnosis-type-container").hide();
        }

        if (jq("#evidence-of-medical-intervention").find("select").find(":selected").val() != "90002") {
            jq("#other-medical-intervention-container").hide();
        }

        if (!jq(".166319").find('#other').find(':checkbox').prop('checked')) {
            jq("#specify-other-container").hide();
        }

        $allRows = jq(this).find('.multi-drug');
        $allRows.hide();
        jq('#1-removeEntry').remove();
        jq('#10-addEntry').remove();
        jq('#1-toggleContainer').show();

        for (let x = 0; x<= 10; x++) {
            if (jq(this).find('#' +x+ '-drug').find("select").find(":selected").val()!='') {
                jq(this).find('#' +x+ '-toggleContainer').show();
                jq(this).find('#' +x+ '-addEntry').hide();
            } else {
                x-=1;
                jq(this).find('#' +x+ '-addEntry').show();
                break;
            }
        }

        jq('.addEntry').on("click", function () {
            var correctedAddButtonId = parseFloat(this.id) + 1;
            var contentAddId = "#" + correctedAddButtonId + "-toggleContainer";
            jq(contentAddId).toggle(true);
            jq('#' + this.id).toggle(false);
            jq('#' + parseFloat(this.id) + '-removeEntry').toggle(true);

            return false;
        });

        jq('.removeEntry').on("click", function () {
            var correctedRemoveButtonId = parseFloat(this.id) - 1;
            var contentAddId = "#" + parseFloat(this.id) + "-toggleContainer";
            jq(contentAddId).toggle(false);
            jq(':input:not(:button)', contentAddId).val([]);
            jq('#' + correctedRemoveButtonId + '-addEntry').toggle(true);
            jq('#' + correctedRemoveButtonId + '-removeEntry').toggle(true);

            return false;
        });


        $allRows_vitals = jq(this).find('.multi-vitals');
        $allRows_vitals.hide();
        jq('#1-removeEntry-vital').remove();
        jq('#10-addEntry-vital').remove();
        jq('#1-toggleContainer-vital').show();

        jq('#11-removeEntry-vital').remove();
        jq('#20-addEntry-vital').remove();
        jq('#11-toggleContainer-vital').show();

        for (let x = 0; x<= 10; x++) {
            if (jq(this).find('.' +x+ '-vitals').find("input[type$='text']").val() != '') {
                jq(this).find('#' +x+ '-toggleContainer-vital').show();
                jq(this).find('#' +x+ '-addEntry-vital').hide();
            } else {
                x-=1;
                jq(this).find('#' +x+ '-addEntry-vital').show();
                break;
            }
        }

        jq('.addEntry-vital').on("click", function () {
            var correctedAddButtonId = parseFloat(this.id) + 1;
            var contentAddId = "#" + correctedAddButtonId + "-toggleContainer-vital";
            jq(contentAddId).toggle(true);
            jq('#' + this.id).toggle(false);
            jq('#' + parseFloat(this.id) + '-removeEntry-vital').toggle(true);

            return false;
        });

        jq('.removeEntry-vital').on("click", function () {
            var correctedRemoveButtonId = parseFloat(this.id) - 1;
            var contentAddId = "#" + parseFloat(this.id) + "-toggleContainer-vital";
            jq(contentAddId).toggle(false);
            jq(':input:not(:button)', contentAddId).val([]);
            jq('#' + correctedRemoveButtonId + '-addEntry-vital').toggle(true);
            jq('#' + correctedRemoveButtonId + '-removeEntry-vital').toggle(true);

            return false;
        });

    });
}

htmlform.script:
if (jQuery) {

    jq(document).ready(function () {

        //Block Multiple encounter entry on same date


        jq(".166319").change(function () {
            if (jq(this).find('#other').find(':checkbox').prop('checked')) {
                enable_fields("specify");
                jq("#specify-other-container").show();
            } else {
                disable_fields("specify");
                jq("#specify-other-container").hide();
            }
        });

        jq("#covid-confirmed").click(function () {
            if (jq(this).find("input[type='radio']:checked").val() == "90003") {
                enable_fields("date-of-diagnosis");
                enable_fields("how-was-the-diagnosis-confirmed");
                jq("#date-of-diagnosis-container").show();
                jq("#how-was-the-diagnosis-confirmed-container").show();
            } else {
                disable_fields("date-of-diagnosis");
                disable_fields("how-was-the-diagnosis-confirmed");
                jq("#date-of-diagnosis-container").hide();
                jq("#how-was-the-diagnosis-confirmed-container").hide();
            }
        });

        jq("#covid-confirmed").click(function () {
            if (jq(this).find("input[type='radio']:checked").val() == "90004") {
                enable_fields("diagnosis-type");
                jq("#diagnosis-type-container").show();
            } else {
                disable_fields("diagnosis-type");
                jq("#diagnosis-type-container").hide();
            }
        });

        jq("#evidence-of-medical-intervention").change(function () {
            if (jq(this).find("select").find(":selected").val() == "90002") {
                enable_fields("other-medical-intervention");
                jq("#other-medical-intervention-container").show();
            } else {
                disable_fields("other-medical-intervention");
                jq("#other-medical-intervention-container").hide();
            }
        });

        //change results for a selected test
        jq("#165844_1").change(function () {

            if(jq(this).find("select").find(":selected").val() == "90225") {
                jq("#smear-results_1").removeClass('hidden'); 
            } else {
                jq("#smear-results_1").addClass('hidden');
            }

            if(jq(this).find("select").find(":selected").val() == "165413") {
                jq("#geneexper-results_1").removeClass('hidden'); 
            } else {
                jq("#geneexper-results_1").addClass('hidden');
            }

             if(jq(this).find("select").find(":selected").val() == "165416") {
                jq("#tblam-results_1").removeClass('hidden'); 
            } else {
                jq("#tblam-results_1").addClass('hidden');
            }

            if(jq(this).find("select").find(":selected").val() == "12") {
                jq("#xray-results_1").removeClass('hidden'); 
            } else {
                jq("#xray-results_1").addClass('hidden');
            }
        });

        jq("#165844_2").change(function () {

            if(jq(this).find("select").find(":selected").val() == "90225") {
                jq("#smear-results_2").removeClass('hidden'); 
            } else {
                jq("#smear-results_2").addClass('hidden');
