ifMode.script:
if (jQuery) {
    jq(document).ready(function () {
        disable_fields("Haemoglobin-results");
        disable_fields("WBC-Count-results");
        disable_fields("Haematocrit-results");
        disable_fields("Platelets-results");
        disable_fields("APTT-APTR-results");
        disable_fields("PT-results");
        disable_fields("INR-results");
        disable_fields("ALT-SGPT-results");
        disable_fields("Total-Bilirubin-results");
        disable_fields("AST-SGOT-results");
        disable_fields("Urea-results");
        disable_fields("Lactate-results");
        disable_fields("Creatinine-results");
        disable_fields("Sodium-results");
        disable_fields("Potassium-results");
        disable_fields("Procalcitonin-results");
        disable_fields("CRP-results");
        disable_fields("LDH-results");
        disable_fields("Creatine-results");
        disable_fields("Kinase-results");
        disable_fields("UTroponin-results");
        disable_fields("ESR-results");
        disable_fields("UD-dimer-results");
        disable_fields("mFerritin-results");
        disable_fields("aIL-6-results");

        jq("#other-occupation-container").hide();
        jq("#oxygen-flow-container").hide();
        jq("#oxygen-source-container").hide();
        jq("#oxygen-interface-container").hide();
        jq("#worst-pressure-support-container").hide();
        jq("#worst-fio-ratio-container").hide();
        jq("#worst-spo-ratio-container").hide();
        jq("#gas-container").hide();

        $allRows = jq(this).find('.multi-drug');
        $allRows.hide();
        jq('#1-removeEntry').remove();
        jq('#10-addEntry').remove();
        jq('#1-toggleContainer').show();

        jq('#11-removeEntry').remove();
        jq('#20-addEntry').remove();
        jq('#11-toggleContainer').show();

        jq('.addEntry').on("click", function () {
            var correctedAddButtonId = parseFloat(this.id) + 1;
            var contentAddId = "#" + correctedAddButtonId + "-toggleContainer";
            jq(contentAddId).toggle(true);
            jq('#' + this.id).toggle(false);
            jq('#' + parseFloat(this.id) + '-removeEntry').toggle(true);

            return false;
        });

        jq('.removeEntry').on("click", function () {
            var correctedRemoveButtonId = parseFloat(this.id) - 1;
            var contentAddId = "#" + parseFloat(this.id) + "-toggleContainer";
            jq(contentAddId).toggle(false);
            jq(':input:not(:button)', contentAddId).val([]);
            jq('#' + correctedRemoveButtonId + '-addEntry').toggle(true);
            jq('#' + correctedRemoveButtonId + '-removeEntry').toggle(true);

            return false;
        });

        $allRows_other = jq(this).find('.multi-drug-other');
        $allRows_other.hide();
        jq('#1-removeEntry-other').remove();
        jq('#10-addEntry-other').remove();
        jq('#1-toggleContainer-other').show();

        jq('#11-removeEntry-other').remove();
        jq('#20-addEntry-other').remove();
        jq('#11-toggleContainer-other').show();

        jq('.addEntry-other').on("click", function () {
            var correctedAddButtonId = parseFloat(this.id) + 1;
            var contentAddId = "#" + correctedAddButtonId + "-toggleContainer-other";
            jq(contentAddId).toggle(true);
            jq('#' + this.id).toggle(false);
            jq('#' + parseFloat(this.id) + '-removeEntry-other').toggle(true);

            return false;
        });

        jq('.removeEntry-other').on("click", function () {
            var correctedRemoveButtonId = parseFloat(this.id) - 1;
            var contentAddId = "#" + parseFloat(this.id) + "-toggleContainer-other";
            jq(contentAddId).toggle(false);
            jq(':input:not(:button)', contentAddId).val([]);
            jq('#' + correctedRemoveButtonId + '-addEntry-other').toggle(true);
            jq('#' + correctedRemoveButtonId + '-removeEntry-other').toggle(true);

            return false;
        });

        $allRows_essential = jq(this).find('.multi-drug-essential');
        $allRows_essential.hide();
        jq('#1-removeEntry-essential').remove();
        jq('#10-addEntry-essential').remove();
        jq('#1-toggleContainer-essential').show();

        jq('#11-removeEntry-essential').remove();
        jq('#20-addEntry-essential').remove();
        jq('#11-toggleContainer-essential').show();

        jq('.addEntry-essential').on("click", function () {
            var correctedAddButtonId = parseFloat(this.id) + 1;
            var contentAddId = "#" + correctedAddButtonId + "-toggleContainer-essential";
            jq(contentAddId).toggle(true);
            jq('#' + this.id).toggle(false);
            jq('#' + parseFloat(this.id) + '-removeEntry-essential').toggle(true);

            return false;
        });

        jq('.removeEntry-essential').on("click", function () {
            var correctedRemoveButtonId = parseFloat(this.id) - 1;
            var contentAddId = "#" + parseFloat(this.id) + "-toggleContainer-essential";
            jq(contentAddId).toggle(false);
            jq(':input:not(:button)', contentAddId).val([]);
            jq('#' + correctedRemoveButtonId + '-addEntry-essential').toggle(true);
            jq('#' + correctedRemoveButtonId + '-removeEntry-essential').toggle(true);

            return false;
        });

        $allRows_vitals = jq(this).find('.multi-vitals');
        $allRows_vitals.hide();
        jq('#1-removeEntry-vital').remove();
        jq('#10-addEntry-vital').remove();
        jq('#1-toggleContainer-vital').show();

        jq('#11-removeEntry-vital').remove();
        jq('#20-addEntry-vital').remove();
        jq('#11-toggleContainer-vital').show();

        jq('.addEntry-vital').on("click", function () {
            var correctedAddButtonId = parseFloat(this.id) + 1;
            var contentAddId = "#" + correctedAddButtonId + "-toggleContainer-vital";
            jq(contentAddId).toggle(true);
            jq('#' + this.id).toggle(false);
            jq('#' + parseFloat(this.id) + '-removeEntry-vital').toggle(true);

            return false;
        });

        jq('.removeEntry-vital').on("click", function () {
            var correctedRemoveButtonId = parseFloat(this.id) - 1;
            var contentAddId = "#" + parseFloat(this.id) + "-toggleContainer-vital";
            jq(contentAddId).toggle(false);
            jq(':input:not(:button)', contentAddId).val([]);
            jq('#' + correctedRemoveButtonId + '-addEntry-vital').toggle(true);
            jq('#' + correctedRemoveButtonId + '-removeEntry-vital').toggle(true);

            return false;
        });
    });
}

if (jQuery) {
    jq(document).ready(function () {

        $allRows = jq(this).find('.multi-drug');
        $allRows.hide();
        jq('#1-removeEntry').remove();
        jq('#10-addEntry').remove();
        jq('#1-toggleContainer').show();

        for (let x = 0; x<= 10; x++) {
            if (jq(this).find('#' +x+ '-drug').find("input[type$='text']").val()!='') {
                jq(this).find('#' +x+ '-toggleContainer').show();
                jq(this).find('#' +x+ '-addEntry').hide();
            } else {
                x-=1;
                jq(this).find('#' +x+ '-addEntry').show();
                break;
            }
        }

        jq('.addEntry').on("click", function () {
            var correctedAddButtonId = parseFloat(this.id) + 1;
            var contentAddId = "#" + correctedAddButtonId + "-toggleContainer";
            jq(contentAddId).toggle(true);
            jq('#' + this.id).toggle(false);
            jq('#' + parseFloat(this.id) + '-removeEntry').toggle(true);

            return false;
        });

        jq('.removeEntry').on("click", function () {
            var correctedRemoveButtonId = parseFloat(this.id) - 1;
            var contentAddId = "#" + parseFloat(this.id) + "-toggleContainer";
            jq(contentAddId).toggle(false);
            jq(':input:not(:button)', contentAddId).val([]);
            jq('#' + correctedRemoveButtonId + '-addEntry').toggle(true);
            jq('#' + correctedRemoveButtonId + '-removeEntry').toggle(true);

            return false;
        });



        $allRows_vitals = jq(this).find('.multi-vitals');
        $allRows_vitals.hide();
        jq('#1-removeEntry-vital').remove();
        jq('#10-addEntry-vital').remove();
        jq('#1-toggleContainer-vital').show();

        jq('#11-removeEntry-vital').remove();
        jq('#20-addEntry-vital').remove();
        jq('#11-toggleContainer-vital').show();


        for (let x = 0; x<= 10; x++) {
            if (jq(this).find('#' +x+ '-vitals').find("select").find(":selected").val() != '') {
                jq(this).find('#' +x+ '-toggleContainer-vital').show();
                jq(this).find('#' +x+ '-addEntry-vital').hide();
            } else {
                x-=1;
                jq(this).find('#' +x+ '-addEntry-vital').show();
                break;
            }
        }

        jq('.addEntry').on("click", function () {
        var correctedAddButtonId = parseFloat(this.id) + 1;
        var contentAddId = "#" + correctedAddButtonId + "-toggleContainer-vital";
        jq(contentAddId).toggle(true);
        jq('#' + this.id).toggle(false);
        jq('#' + parseFloat(this.id) + '-removeEntry-vital').toggle(true);

        return false;
        });

        jq('.removeEntry').on("click", function () {
        var correctedRemoveButtonId = parseFloat(this.id) - 1;
        var contentAddId = "#" + parseFloat(this.id) + "-toggleContainer-vital";
        jq(contentAddId).toggle(false);
        jq(':input:not(:button)', contentAddId).val([]);
        jq('#' + correctedRemoveButtonId + '-addEntry-vital').toggle(true);
        jq('#' + correctedRemoveButtonId + '-removeEntry-vital').toggle(true);

        return false;
        });



        $allRows_other = jq(this).find('.multi-drug-other');
        $allRows_other.hide();
        jq('#1-removeEntry-other').remove();
        jq('#10-addEntry-other').remove();
        jq('#1-toggleContainer-other').show();

        for (let x = 0; x<= 10; x++) {
            if (jq(this).find('#' +x+ '-drugother').find("input[type$='text']").val()!='') {
                jq(this).find('#' +x+ '-toggleContainer-other').show();
                jq(this).find('#' +x+ '-addEntry-other').hide();
            } else {
                x-=1;
                jq(this).find('#' +x+ '-addEntry-other').show();
                break;
            }
        }

        jq('.addEntry-other').on("click", function () {
            var correctedAddButtonId = parseFloat(this.id) + 1;
            var contentAddId = "#" + correctedAddButtonId + "-toggleContainer-other";
            jq(contentAddId).toggle(true);
            jq('#' + this.id).toggle(false);
            jq('#' + parseFloat(this.id) + '-removeEntry-other').toggle(true);

            return false;
        });

        jq('.removeEntry-other').on("click", function () {
            var correctedRemoveButtonId = parseFloat(this.id) - 1;
            var contentAddId = "#" + parseFloat(this.id) + "-toggleContainer-other";
            jq(contentAddId).toggle(false);
            jq(':input:not(:button)', contentAddId).val([]);
            jq('#' + correctedRemoveButtonId + '-addEntry-other').toggle(true);
            jq('#' + correctedRemoveButtonId + '-removeEntry-other').toggle(true);

            return false;
        });



        $allRows_other = jq(this).find('.multi-drug-essential');
        $allRows_other.hide();
        jq('#1-removeEntry-essential').remove();
        jq('#10-addEntry-essential').remove();
        jq('#1-toggleContainer-essential').show();
        jq('#11-removeEntry-essential').remove();
        jq('#20-addEntry-essential').remove();
        jq('#11-toggleContainer-essential').show();

        for (let x = 0; x<= 10; x++) {
            if (jq(this).find('#' +x+ '-drug-essential').find("input[type$='text']").val()!='') {
                jq(this).find('#' +x+ '-toggleContainer-essential').show();
                jq(this).find('#' +x+ '-addEntry-essential').hide();
            } else {
                x-=1;
                jq(this).find('#' +x+ '-addEntry-essential').show();
                break;
            }
        }

        jq('.addEntry-essential').on("click", function () {
            var correctedAddButtonId = parseFloat(this.id) + 1;
            var contentAddId = "#" + correctedAddButtonId + "-toggleContainer-essential";
            jq(contentAddId).toggle(true);
            jq('#' + this.id).toggle(false);
            jq('#' + parseFloat(this.id) + '-removeEntry-essential').toggle(true);

            return false;
        });

        jq('.removeEntry-essential').on("click", function () {
            var correctedRemoveButtonId = parseFloat(this.id) - 1;
            var contentAddId = "#" + parseFloat(this.id) + "-toggleContainer-essential";
            jq(contentAddId).toggle(false);
            jq(':input:not(:button)', contentAddId).val([]);
            jq('#' + correctedRemoveButtonId + '-addEntry-essential').toggle(true);
            jq('#' + correctedRemoveButtonId + '-removeEntry-essential').toggle(true);

            return false;
        });


        if (jq("#occupation").find("select").find(":selected").val() != "5622") {
            jq("#other-occupation-container").hide();
        }

        if(!jq(".lab-tests").find('#Haemoglobin-test').find(':checkbox').prop('checked')) {
            disable_fields("Haemoglobin-results");
        }

        if(!jq(".lab-tests").find('#WBC-Count-test').find(':checkbox').prop('checked')) {
            disable_fields("WBC-Count-results");
        }

        if(!jq(".lab-tests").find('#Haematocrit-test').find(':checkbox').prop('checked')) {
            disable_fields("Haematocrit-results");
        }

        if(!jq(".lab-tests").find('#Platelets-test').find(':checkbox').prop('checked')) {
            disable_fields("Platelets-results");
        }

        if(!jq(".lab-tests").find('#APTT-APTR-test').find(':checkbox').prop('checked')) {
            disable_fields("APTT-APTR-results");
        }

        if(!jq(".lab-tests").find('#PT-test').find(':checkbox').prop('checked')) {
            disable_fields("PT-results");
        }

        if(!jq(".lab-tests").find('#INR-test').find(':checkbox').prop('checked')) {
            disable_fields("INR-results");
        }

        if(!jq(".lab-tests").find('#ALT-SGPT-test').find(':checkbox').prop('checked')) {
            disable_fields("ALT-SGPT-results");
        }

        if(!jq(".lab-tests").find('#Total-Bilirubin-test').find(':checkbox').prop('checked')) {
            disable_fields("Total-Bilirubin-results");
        }

        if(!jq(".lab-tests").find('#AST-SGOT-test').find(':checkbox').prop('checked')) {
            disable_fields("AST-SGOT-results");
        }

        if(!jq(".lab-tests").find('#Urea-test').find(':checkbox').prop('checked')) {
            disable_fields("Urea-results");
        }

        if(!jq(".lab-tests").find('#Lactate-test').find(':checkbox').prop('checked')) {
            disable_fields("Lactate-results");
        }

        if(!jq(".lab-tests").find('#Creatinine-test').find(':checkbox').prop('checked')) {
            disable_fields("Creatinine-results");
        }

        if(!jq(".lab-tests").find('#Sodium-test').find(':checkbox').prop('checked')) {
            disable_fields("Sodium-results");
        }

        if(!jq(".lab-tests").find('#Potassium-test').find(':checkbox').prop('checked')) {
            disable_fields("Potassium-results");
        }

        if(!jq(".lab-tests").find('#Procalcitonin-test').find(':checkbox').prop('checked')) {
            disable_fields("Procalcitonin-results");
        }

        if(!jq(".lab-tests").find('#CRP-test').find(':checkbox').prop('checked')) {
            disable_fields("CRP-results");
        }

        if(!jq(".lab-tests").find('#LDH-test').find(':checkbox').prop('checked')) {
            disable_fields("LDH-results");
        }

        if(!jq(".lab-tests").find('#Creatine-test').find(':checkbox').prop('checked')) {
            disable_fields("Creatine-results");
        }

        if(!jq(".lab-tests").find('#UTroponin-test').find(':checkbox').prop('checked')) {
            disable_fields("UTroponin-results");
        }

        if(!jq(".lab-tests").find('#ESR-test').find(':checkbox').prop('checked')) {
            disable_fields("ESR-results");
        }

        if(!jq(".lab-tests").find('#UD-dimer-test').find(':checkbox').prop('checked')) {
        disable_fields("UD-dimer-results");
        }

        if(!jq(".lab-tests").find('#mFerritin-test').find(':checkbox').prop('checked')) {
        disable_fields("mFerritin-results");
        }

        if(!jq(".lab-tests").find('#aIL-6-test').find(':checkbox').prop('checked')) {
        disable_fields("aIL-6-results");
        }

        if (jq("#oxygen-therapy").find("input[type='radio']:checked").val() != "90003") {
            jq("#oxygen-flow-container").hide();
            jq("#oxygen-source-container").hide();
            jq("#oxygen-interface-container").hide();
        }

        if (jq("#non-invasive-ventilation").find("input[type='radio']:checked").val() != "90003") {
            jq("#worst-pressure-support-container").hide();
        }

        if (jq("#invasive-ventilation").find("input[type='radio']:checked").val() != "90003") { ;
            jq("#worst-fio-ratio-container").hide();
            jq("#worst-spo-ratio-container").hide();
        }

        if (jq("#blood-gas-done").find("input[type='radio']:checked").val() != "90003") {
            jq("#gas-container").hide();
        }

    });
