htmlform.script:
    if (jQuery) {
        jq(document).ready(function () {
            jq("#occupation").change(function () {
                if (jq(this).find("select").find(":selected").val() == "5622") {
                    enable_fields("other-occupation");
                    jq("#other-occupation-container").show();
                } else {
                    disable_fields("other-occupation");
                    jq("#other-occupation-container").hide();
                }
            });

           jq(".facility-details").change(function () {
                if (jq(this).find('#other-facility-details').find(':checkbox').prop('checked')) {
                    enable_fields("specify-other-facility-details");
                } else {
                    disable_fields("specify-other-facility-details");
                }
           });

           jq("#corticosteroid").click(function () {
                if (jq(this).find("input[type='radio']:checked").val() == "90003") {
                    enable_fields("corticosteroid-route");
                } else {
                    disable_fields("corticosteroid-route");
                }
           });

           jq("#malaria-treatment").click(function () {
                if (jq(this).find("input[type='radio']:checked").val() == "90003") {
                    enable_fields("specify-other-malaria-treatment");
                } else {
                    disable_fields("specify-other-malaria-treatment");
                }
           });

           jq(".if-art").change(function () {
                if (jq(this).find('#other-art').find(':checkbox').prop('checked')) {
                    enable_fields("specify-other-art-regimens");
                } else {
                    disable_fields("specify-other-art-regimens");
                }
           });

           jq("#diabetes-medication").click(function () {
                if (jq(this).find("input[type='radio']:checked").val() == "90003") {
                    enable_fields("specify-other-diabetes-medication");
                } else {
                    disable_fields("specify-other-diabetes-medication");
                }
           });

           jq("#satin").click(function () {
                if (jq(this).find("input[type='radio']:checked").val() == "90003") {
                    enable_fields("specify-other-satin");
                } else {
                    disable_fields("specify-other-satin");
                }
           });

           jq("#anticoagulation").click(function () {
                if (jq(this).find("input[type='radio']:checked").val() == "90003") {
                    enable_fields("specify-other-anticoagulation");
                } else {
                    disable_fields("specify-other-anticoagulation");
                }
           });

           jq("#non-invasive-ventilation").click(function () {
                if (jq(this).find("input[type='radio']:checked").val() == "90003") {
                    enable_fields("worst-pressure-support");
                    jq("#worst-pressure-support-container").show();
                } else {
                    disable_fields("worst-pressure-support");
                    jq("#worst-pressure-support-container").hide();
                }
           });

           jq("#invasive-ventilation").click(function () {
                if (jq(this).find("input[type='radio']:checked").val() == "90003") {
                    enable_fields("worst-fio-ratio");
                    enable_fields("worst-spo-ratio");
                    jq("#worst-fio-ratio-container").show();
                    jq("#worst-spo-ratio-container").show();
                } else {
                    disable_fields("worst-fio-ratio");
                    disable_fields("worst-spo-ratio");
                    jq("#worst-fio-ratio-container").hide();
                    jq("#worst-spo-ratio-container").hide();
                }
           });

           jq("#blood-gas-done").click(function () {
                if (jq(this).find("input[type='radio']:checked").val() == "90003") {
                    enable_fields("pH");
                    enable_fields("PaO2");
                    enable_fields("PCO2");
                    enable_fields("HCO3");
                    jq("#gas-container").show();
                } else {
                    disable_fields("pH");
                    disable_fields("PaO2");
                    disable_fields("PCO2");
                    disable_fields("HCO3");
                    jq("#gas-container").hide();
                }
           });

            jq(".complications").change(function () {
                if (jq(this).find('#other-complications').find(':checkbox').prop('checked')) {
                    enable_fields("specify-other-complications");
                } else {
                    disable_fields("specify-other-complications");
                }
            });

            jq(".antibiotics").change(function () {
                if (jq(this).find('#other-anti-biotics').find(':checkbox').prop('checked')) {
                    enable_fields("specify-other-anti-biotics");
                } else {
                    disable_fields("specify-other-anti-biotics");
                }
            });

            jq("#chest-xray").click(function () {
                if (jq(this).find("input[type='radio']:checked").val() == "90003") {
                    enable_fields("infiltrates-present");
                } else {
                    disable_fields("infiltrates-present");
                }
            });

            jq("#oxygen-therapy").click(function () {
                if (jq(this).find("input[type='radio']:checked").val() == "90003") {
                    enable_fields("oxygen-flow");
                    enable_fields("oxygen-source");
                    enable_fields("oxygen-interface");
                    jq("#oxygen-flow-container").show();
                    jq("#oxygen-source-container").show();
                    jq("#oxygen-interface-container").show();
                } else {
                    disable_fields("oxygen-flow");
                    disable_fields("oxygen-source");
                    disable_fields("oxygen-interface");
                    jq("#oxygen-flow-container").hide();
                    jq("#oxygen-source-container").hide();
                    jq("#oxygen-interface-container").hide();
                }
            });

            jq(".hypertension-medication").change(function () {
                if (jq(this).find('#other-hypertension-medication').find(':checkbox').prop('checked')) {
                    enable_fields("specify-other-hypertension-medication");
                } else {
                    disable_fields("specify-other-hypertension-medication");
                }
            });

            jq(".daily-clinical-features").change(function () {
                if (jq(this).find('#other-clinical-features').find(':checkbox').prop('checked')) {
                    enable_fields("specify-other-clinical-features");
                } else {
                    disable_fields("specify-other-clinical-features");
                }
            });

            jq(".lab-tests").change(function () {
                if(jq(this).find('#Haemoglobin-test').find(':checkbox').prop('checked')) {
                    enable_fields("Haemoglobin-results");
                } else {
                    disable_fields("Haemoglobin-results");
                }

                if(jq(this).find('#WBC-Count-test').find(':checkbox').prop('checked')) {
                    enable_fields("WBC-Count-results");
                } else {
                    disable_fields("WBC-Count-results");
                }

                if(jq(this).find('#Haematocrit-test').find(':checkbox').prop('checked')) {
                    enable_fields("Haematocrit-results");
                } else {
                    disable_fields("Haematocrit-results");
                }

                if(jq(this).find('#Platelets-test').find(':checkbox').prop('checked')) {
                    enable_fields("Platelets-results");
                } else {
                    disable_fields("Platelets-results");
                }

                if(jq(this).find('#APTT-APTR-test').find(':checkbox').prop('checked')) {
                    enable_fields("APTT-APTR-results");
                } else {
                    disable_fields("APTT-APTR-results");
                }

                if(jq(this).find('#PT-test').find(':checkbox').prop('checked')) {
                    enable_fields("PT-results");
                } else {
                    disable_fields("PT-results");
                }

                if(jq(this).find('#INR-test').find(':checkbox').prop('checked')) {
                    enable_fields("INR-results");
                } else {
                    disable_fields("INR-results");
                }

                if(jq(this).find('#ALT-SGPT-test').find(':checkbox').prop('checked')) {
                    enable_fields("ALT-SGPT-results");
                } else {
                    disable_fields("ALT-SGPT-results");
                }

                if(jq(this).find('#Total-Bilirubin-test').find(':checkbox').prop('checked')) {
                    enable_fields("Total-Bilirubin-results");
                } else {
                    disable_fields("Total-Bilirubin-results");
                }

                if(jq(this).find('#AST-SGOT-test').find(':checkbox').prop('checked')) {
                    enable_fields("AST-SGOT-results");
                } else {
                    disable_fields("AST-SGOT-results");
                }

                if(jq(this).find('#Urea-test').find(':checkbox').prop('checked')) {
                    enable_fields("Urea-results");
                } else {
                    disable_fields("Urea-results");
                }

                if(jq(this).find('#Lactate-test').find(':checkbox').prop('checked')) {
                    enable_fields("Lactate-results");
                } else {
                    disable_fields("Lactate-results");
                }

                if(jq(this).find('#Creatinine-test').find(':checkbox').prop('checked')) {
                    enable_fields("Creatinine-results");
                } else {
                    disable_fields("Creatinine-results");
                }

                if(jq(this).find('#Sodium-test').find(':checkbox').prop('checked')) {
                    enable_fields("Sodium-results");
                } else {
                    disable_fields("Sodium-results");
                }

                if(jq(this).find('#Potassium-test').find(':checkbox').prop('checked')) {
                    enable_fields("Potassium-results");
                } else {
                    disable_fields("Potassium-results");
                }

                if(jq(this).find('#Procalcitonin-test').find(':checkbox').prop('checked')) {
                    enable_fields("Procalcitonin-results");
                } else {
                    disable_fields("Procalcitonin-results");
                }

                if(jq(this).find('#CRP-test').find(':checkbox').prop('checked')) {
                    enable_fields("CRP-results");
                } else {
                    disable_fields("CRP-results");
                }

                if(jq(this).find('#LDH-test').find(':checkbox').prop('checked')) {
                    enable_fields("LDH-results");
                } else {
                    disable_fields("LDH-results");
                }

                if(jq(this).find('#Creatine-test').find(':checkbox').prop('checked')) {
                    enable_fields("Creatine-results");
                } else {
                    disable_fields("Creatine-results");
                }



                if(jq(this).find('#UTroponin-test').find(':checkbox').prop('checked')) {
                    enable_fields("UTroponin-results");
                } else {
                    disable_fields("UTroponin-results");
                }

                if(jq(this).find('#ESR-test').find(':checkbox').prop('checked')) {
                    enable_fields("ESR-results");
                } else {
                    disable_fields("ESR-results");
                }

                if(jq(this).find('#UD-dimer-test').find(':checkbox').prop('checked')) {
                    enable_fields("UD-dimer-results");
                } else {
                    disable_fields("UD-dimer-results");
                }

                if(jq(this).find('#mFerritin-test').find(':checkbox').prop('checked')) {
                    enable_fields("mFerritin-results");
                } else {
                    disable_fields("mFerritin-results");
                }

                if(jq(this).find('#aIL-6-test').find(':checkbox').prop('checked')) {
                    enable_fields("aIL-6-results");
                } else {
                    disable_fields("aIL-6-results");
                }
        });
    });
