        /* add validation rule to ensure that the chart is opened after the birthdate of the child */
        jq.validator.addMethod("birthBeforeChartOpenedDate", function (value, element) {
            var birthDate = new Date(jq('#birthDate').text());
            var dateOpened = new Date(getValue('encounterDate.value'));
            return dateOpened >= birthDate;

        }, "The date file opened has to be after the date of birth of the exposed infant");


        /* add the validation rule */
        jq("#encounterDate input").addClass("birthBeforeChartOpenedDate");

        /* add the validator on the beforeSubmit */
        beforeSubmit.push(function () {

            var dateValidationErrors = [];

            /* Repeat PCR can be done after the first PCR results */
            if (getValue('first-pcr-results.value') == 703) {
                dateValidationErrors.push(dateValidator("repeat-pcr-date", "first-pcr-date", "", "The repeat PCR date must be after the date of the 1st PCR", "", "less_than", true));
            }

            /* Validate the 2nd PCR date if the 1st PCR date is provided */
            if (getValue('first-pcr-date.value') != '') {
                dateValidationErrors.push(dateValidator("second-pcr-date", "first-pcr-date", "", "The date of the second PCR must be after the date of the 1st PCR", "", "less_than", true));
            }

            /* Repeat PCR can be done after the 2nd PCR results */
            if (getValue('second-pcr-results.value') == 703) {
                dateValidationErrors.push(dateValidator("repeat-pcr-date", "second-pcr-date", "", "The repeat PCR date must be after the date of the 2nd PCR", "", "less_than", true));
            }

            /* Validate the Rapid Test date if the 2nd PCR date is provided */
            if (getValue('second-pcr-date.value') != '') {
                dateValidationErrors.push(dateValidator("rapid-pcr-date", "second-pcr-date", "", "The rapid test date must be after the date of the 2nd PCR", "", "less_than", true));
            }

            /* Repeat PCR can be done after the 3rd PCR results */
            if (getValue('third-pcr-results.value') == 703) {
                dateValidationErrors.push(dateValidator("repeat-pcr-date", "third-pcr-date", "", "The repeat PCR date must be after the date of the 3rd PCR", "", "less_than", true));
            }

            /* Validate the 3rd PCR date if the 1st PCR date is provided */
            if (getValue('first-pcr-date.value') != '') {
                dateValidationErrors.push(dateValidator("third-pcr-date", "first-pcr-date", "", "The date of the third PCR must be after the date of the 1st PCR", "", "less_than", true));
            }

            /* Validate the 3rd PCR date if the 2nd PCR date is provided */
            if (getValue('second-pcr-date.value') != '') {
                dateValidationErrors.push(dateValidator("third-pcr-date", "second-pcr-date", "", "The date of the third PCR must be after the date of the 2nd PCR", "", "less_than", true));
            }

            /* Validate the Rapid Test date if the 3rd PCR date is provided */
            if (getValue('third-pcr-date.value') != '') {
                dateValidationErrors.push(dateValidator("rapid-pcr-date", "third-pcr-date", "", "The rapid test date must be after the date of the 3rd PCR", "", "less_than", true));
            }

            /* Validate the 2nd follow up attempt date if the 1st follow up attempt date is provided */
            if (getValue('first-followup-date.value') != '') {
                dateValidationErrors.push(dateValidator("second-followup-date", "first-followup-date", "", "The date of the Second follow up must be after the date of the first follow up", "", "less_than", true));
            }

            /* Validate the 3rd follow up attempt date if the 2nd follow up attempt date is provided */
            if (getValue('second-followup-date.value') != '') {
                dateValidationErrors.push(dateValidator("third-followup-date", "second-followup-date", "", "The date of the third follow up must be after the date of the second follow up", "", "less_than", true));
            }

            /* Validate the 3rd follow up attempt date if the 1st follow up attempt date is provided */
            if (getValue('first-followup-date.value') != '') {
                dateValidationErrors.push(dateValidator("third-followup-date", "first-followup-date", "", "The date of the third follow up must be after the date of the first follow up", "", "less_than", true));
            }

            if (dateValidationErrors.indexOf(false) != -1) {
                jq().toastmessage('showErrorToast', "There are errors during the validation of the data on the form. Please correct them and submit again");
                return false;
            } 

            jq("#htmlform").validate();
            return jq("#htmlform").valid();
        });

