    function getDrugByConcept(concept, drug_strength_id) {
        if (enableStockManagement) {
            jq("#" + drug_strength_id + "").find("select").empty();
            var stockItems = []
            jq.ajax({
                type: "GET",
                url: '/' + OPENMRS_CONTEXT_PATH + "/ws/rest/v1/stockmanagement/stockiteminventory?v=default&limit=10&totalCount=true&conceptId=" + concept + "&groupBy=LocationStockItem&dispenseLocationUuid=" + currentLocationUUID + "&includeStrength=1&includeConceptRefIds=1",
                dataType: "json",
                async: false,
                success: function (data) {
                    stockItems = data.results;
                }
            });
            if (stockItems.length) {
                enable_fields(drug_strength_id)
            }
            jq("#" + drug_strength_id + "").find("select").append($("Select Strength").attr("value", ''));
            for (var i = 0; i < stockItems.length; i++) {
                jq("#" + drug_strength_id + "").find("select").append($("").attr("value", stockItems[i].stockItemUuid).text((stockItems[i].drugName + " | " + stockItems[i].quantity + " " + stockItems[i].quantityUoM + " at " + stockItems[i].partyName).replace("null", " qty ")).attr("location", stockItems[i].locationUuid).attr("strength", stockItems[i].drugStrength).attr("stockItemUUID", stockItems[i].stockItemUuid).attr("drugUuid", stockItems[i].drugUuid));
    }
}
}

function getLocationByName(locationName) {
    var locationUUID
        jq.ajax({
            type: "GET",
            url: '/' + OPENMRS_CONTEXT_PATH + "/ws/rest/v1/location?q=" + locationName,
            dataType: "json",
            async: false,
            success: function (data) {
                locationUUID = data.results[0].uuid
            }
        });
    return locationUUID
}


function getEncountersByEncounterTypeUUID(patientUUID, encounterTypeUUID) {
    var serverResponse = [];
        jq.ajax({
            type: "GET",
            url: '/' + OPENMRS_CONTEXT_PATH + "/ws/rest/v1/encounter?v=full&patient=" + patientUUID + "&encounterType=" + encounterTypeUUID,
            dataType: "json",
            async: false,
            success: function (data) {
                serverResponse = data.results;
            }
        });
    return serverResponse
}

function isPatientTransferedOut(latestTransferInEncounter, latestTransferOutEncounter) {

    if (latestTransferOutEncounter === null) {
        return false;
    } else if (latestTransferInEncounter === null) {
        return true;
    } else if ((new Date(latestTransferInEncounter.encounterDatetime) < new Date(latestTransferOutEncounter.encounterDatetime))) {
        return true
    } else {
        return false;
    }
}

function getCohortCategorizations(uuid) {
    jq('#refill_point_select_id').empty();
    jq.ajax({
        type: "GET",
        url: '/' + OPENMRS_CONTEXT_PATH + "/ws/rest/v1/cohortm/cohort?v=custom:(name,uuid,description,voided)&cohortType=" + uuid,
        dataType: "json",
        contentType: "application/json",
        async: false,
        success: function (data) {
            var cohorts = data.results;
            jq('#refill_point_select_id').append($("").attr("value", ''));
            for (var i = 0; i < cohorts.length; i++) {
                if (cohorts[i].voided === false) {
                    jq('#refill_point_select_id').append($("").attr("value", cohorts[i].uuid).text(cohorts[i].name));
                }
            }

        }
    });
}

//Check if any prescription is made, if so that return date will be mandatory
jq(document).ready(function () {

jq(".prescription-poc").change(function () {
    var prescriptionMade = [];

    //if there is any value in the text box
    var ptext = jq(this).find("input[type$='text']").each(function () {
    if ($(this).val() != "")
    prescriptionMade.push($(this).val());
})

//if there is any radio button checked
jq(this).find("input[type$='radio']:checked").each(function () {
    if ($(this).val() != 1066)
    prescriptionMade.push($(this).val());
})

//if any regimen is selected
var regimen = jq(".regimen-given :selected").each(function () {
    if ($(this).val() != "")
    prescriptionMade.push($(this).val());
})

if (prescriptionMade.length > 0)
    prescription = 'true';
    else
    prescription = 'false';

});
});

