var instabilityCause = "";

    function queryRestData(url, method, data) {
        var responseData = null;
        jq.ajax({
            type: method,
            url: '/' + OPENMRS_CONTEXT_PATH + "/ws/rest/v1/" + url,
            dataType: "json",
            contentType: "application/json",
            accept: "application/json",
            async: false,
            data: data,
            success: function (response) {
                responseData = response;
            },
            error: function (response) {
                responseData = response
            }
        });
        return responseData;
    }

    jq(document).ready(function () {

        jq("#visit-date").addClass("html-form-entry-disabled-field");

        var age = ;

        getMUACCodeFromMUACScoreByAge(age, '1343', '99030');

        //Block Multiple encounter entry on same date
        blockEncounterOnSameDateEncounter(jq("#encounterDate").find("input[type=hidden]"), "block");

        var previousVl = null;
        var previousVlDate = null;
        var currentRegimen = null;
        var regimenBeforeDTG = null;
        var onThirdLine = null;
        var previousAdherence = null;
        var clinicStaging = null;
        var regimenStartedDate = null;
        var regimenBeforeDTGStartedDate = null;
        var sputumDate = null;
        var artStartDate = null;
        var teatmentStartDate = null;
        var sputumValue = null;
        var currentRegimenStartDate = null;

        let searchParams = new URLSearchParams(window.location.search)
        var patientId = searchParams.get('patientId')
        var visitId = searchParams.get('visitId')
        var encounterId = ""

        if (searchParams.get('encounterId') !== null && searchParams.get('encounterId') !== "null") {
            encounterId = "&encounter=" + searchParams.get('encounterId');
        }
        var stability_url = "stabilitycriteria?v=full&patient=" + patientId + "&visit=" + visitId + encounterId
        var global_property_url = "systemsetting?q=ugandaemr.dsdm.allowClinicalOverrideDSDMPatientStability&v=custom:(property,uuid,value)"

        var enableCliniciansMakeStabilityDecisions = queryRestData(global_property_url).results[0].value;
        if (enableCliniciansMakeStabilityDecisions !== true && enableCliniciansMakeStabilityDecisions !== "true") {
            var stability = queryRestData(stability_url)

            if (stability != null && stability.hasOwnProperty("results")) {
                var stabilityResults = stability.results[0];

                previousVl = stabilityResults.vlObs;
                previousVlDate = stabilityResults.vlDateObs;
                artStartDate = stabilityResults.artStartDate;
                currentRegimen = stabilityResults.regimenObsConceptId

                if(stabilityResults.regimenObs!=null){
                    regimenStartedDate = stabilityResults.regimenObs.encounter.encounterDatetime;
                }

                if (stabilityResults.currentRegimenObsConceptId!=null && stabilityResults.regimenObs!=null && stabilityResults.currentRegimenObsConceptId === stabilityResults.baselineRegimenConceptId ) {
                    currentRegimenStartDate = stabilityResults.regimenObs.encounter.encounterDatetime;
                } else {
                    currentRegimenStartDate = null;
                }

                if (stabilityResults.regimenBeforeDTGObs != null) {
                    regimenBeforeDTG = stabilityResults.regimenBeforeDTGObsValueConceptId
                    regimenBeforeDTGStartedDate = stabilityResults.regimenBeforeDTGObs.encounter.encounterDatetime;
                }

                onThirdLine = stabilityResults.onThirdRegimen;

                if(stabilityResults.adherenceObs!=null){
                    stabilityResults.adherenceObs.forEach((it, index) => {
                        previousAdherence = it.value.name.name;
                    });
                }


                clinicStaging = stabilityResults.conceptForClinicStage;
                if (stabilityResults.sputumResultDateObs !== null) {
                    sputumDate = stabilityResults.sputumResultDateObs.valueDate;
                    teatmentStartDate = stabilityResults.sputumResultDateObs.encounter.encounterDatetime;
                    sputumValue = stabilityResults.sputumResultObsValueConceptId;
                }

            }
        }

        var stableArray = [];


        if (regimenBeforeDTG && regimenBeforeDTG !== "") {
            regimenStartedDate = regimenBeforeDTGStartedDate;
        }

disable_fields("duration-on-current-regimen");
