    //This function checks if patient is stable or unstable given the patient`s viral load
    function DSDMStabilityCheck() {

        stableArray = [];
        instabilityCause = "";
        var pageCurrentRegimen = jq("#art-regimen").find("select").find(":selected").val();
        var pageEncounterDate = jq("#encounterDate").find("input[type=hidden]").val();
        var dsdmRegimenCoceptsIds = "164976,164977,164978,164979";

        var allowedCurrentRegimenDuration = ;
        var allowedDurationOnTB = ;
        var viralLoadSuppressionCopies = ;
        var validPeriodInMothsForViralLoad = ;

        if (previousVl === "") {
            stableArray.push(false);
            instabilityCause = instabilityCause + "Patient does not have any Viral Load Record found\n ";
        } else if (previousVl !== "" && periodBetweenDates(new Date(pageEncounterDate), new Date(previousVlDate)) > validPeriodInMothsForViralLoad) {
            stableArray.push(false);
            instabilityCause = instabilityCause + "Patient does not have any Viral Load Record found. The last Viral Load was collected on " + (new Date(previousVlDate)) + "\n";
        } else if (previousVl !== "" && periodBetweenDates(new Date(pageEncounterDate), new Date(previousVlDate)) < validPeriodInMothsForViralLoad && previousVl > viralLoadSuppressionCopies) {
            stableArray.push(false);
            instabilityCause = instabilityCause + "The patient has a viral load of " + previousVl + " copies per mil which is regarded as non suppressed.\n ";
        } else if (previousVl !== "" && previousVl <= viralLoadSuppressionCopies && periodBetweenDates(new Date(pageEncounterDate), new Date(previousVlDate)) <= validPeriodInMothsForViralLoad) {
            stableArray.push(true);
        }

        /* Third Line*/
        if (onThirdLine !== "true") {
            stableArray.push(true);
        } else {
            stableArray.push(false);
            instabilityCause += "Current regimen is a third line regimen \n "
        }

        /* Adherence Check*/
        if(previousAdherence!=null) {
            if (previousAdherence.length <= 0 || previousAdherence.trim().includes("POOR")) {
                stableArray.push(false);
                instabilityCause += "Adherence is not Good \n "
            } else {
                stableArray.push(true);
            }
        }

        /* Clinic staging */
        if (clinicStaging === "90033" || clinicStaging === "90034" || clinicStaging === "90293" || clinicStaging === "90294") {
            stableArray.push(true);
        } else {
            stableArray.push(false);
            instabilityCause += "Clinic stage is neither 1 or 2 \n";
        }


        if (currentRegimen !== "" && (currentRegimen === pageCurrentRegimen || dsdmRegimenCoceptsIds.trim().includes(pageCurrentRegimen)) && periodBetweenDates(new Date(pageEncounterDate), new Date(regimenStartedDate)) > allowedCurrentRegimenDuration) {

            stableArray.push(true);
        } else {
            stableArray.push(false);
            instabilityCause += "Patient has not spent more than" + allowedCurrentRegimenDuration + " months on current Regimen \n "
        }

        if (sputumValue !== "" && sputumValue !== "664" && periodBetweenDates(new Date(jq("#encounterDate").find("input[type=hidden]").val()), new Date(teatmentStartDate)) < allowedDurationOnTB) {
            stableArray.push(false);
            instabilityCause += "Patient has not been on treatment for TB for at least 2 months"
        } else {
            stableArray.push(true);
        }
        instabilityCause.trim().replace()

        if (stableArray.indexOf(false) != -1) {
            jq("#patient-categorization select").val("90004");
        } else {
            jq("#patient-categorization select").val("90003");
        }
    }

    /* enabling and disabling other signs and symptoms */

    jq("#enable-disable-signs-and-symptoms").change(function () {
        if (jq(this).find(':checkbox').prop('checked')) {
            enable_fields("signs-and-symptoms-enabled");
