    beforeSubmit.push(function () {

        var noOfErrorsInPrescription=0;
        jq(".multi-drug").each(function () {
            var id= this.id.replace("-toggleContainer","")
            var element= jq("#"+id+"-toggleContainer");
            var qty_unit=jq(element).find("#qty_unit_" + id+" select").find(":selected").val()
            var duration_unit=jq(element).find("#duration_unit_" + id+" select").find(":selected").val()
            var drug=jq(element).find("#"+id+"-drug input").val();
            var qty=jq(element).find("#quantity_" + id).find("input").val()
            var strength=jq(element).find("#drug_strength_" + id).find("input").val()
            var duration=jq(element).find("#duration_"+id+" input").val();

            if(drug!==null && drug!==""){
                if(qty_unit===null || qty_unit===""){
                    getField('qty_unit_'+id+".error").html('Qty unit is required').show();
                    noOfErrorsInPrescription+=1
                }
                if(duration_unit===null|| duration_unit===""){
                    getField('duration_unit_'+id+".error").html('duration  unit is required').show();
                    noOfErrorsInPrescription+=1
                }
                if(qty===null || qty===""){
                    jq('#quantity_' + id).find(".error").html('quantity  is required').show();
                    noOfErrorsInPrescription+=1
                }

                if(duration===null || duration===""){
                    jq('#duration_' + id).find(".error").html('duration  is required').show();
                    noOfErrorsInPrescription+=1
                }

                if(strength===null || strength===""){
                    jq('#drug_strength_' + id).find(".error").html('strength  is required').show();
                    jq().toastmessage('showErrorToast', "strength for drug "+drug+" is required");
                    noOfErrorsInPrescription+=1
                }
            }
        });

        if(noOfErrorsInPrescription>0){
            jq().toastmessage('showErrorToast', "There are "+noOfErrorsInPrescription+" errors under medication that need to be resolved");
            return false
        }


        setValue('returndate.error', '');
        setValue('viral-load-results-copies.error', '');
        setValue('date-viral-load-taken.error', '');


            setValue('no-of-art-pills.error','');
            setValue('isoniazid-pills.error','');
            setValue('vitamin-b6-pills.error','');
            setValue('160856.error','');
            setValue('fluconazole-pills.error','');
            setValue('other-meds-quantity.error','');


var encounterDate = new Date((getValue('encounterDate.value')));
var appointment = new Date((jq("#returndate").find('input[type=hidden]').val()));
var transferOutDate = new Date((getValue('transfer_out_date.value')));
var transferOut = jq('.transfer_out').find("input[type$='checkbox']:checked").val();
var returnDate = getValue('returndate.value');
var returnDatePoc = jq('.return-date-poc').find("input[type$='text']").val();
var returnDatePocClassLength = jq('.return-date-poc').length;

        if (returnDatePocClassLength > 0) {
            returnDatePoc = jq('.return-date-poc').find("input[type$='text']").val();
        }

        if (getValue('provider.value') == "") {
            jq().toastmessage('showErrorToast', "Attending clinician is required");
        }

if (encounterDate >= appointment) {
getField('returndate.error').html('The return date must be after the date of this visit ' +
formatDateForDisplay(encounterDate)).show();
jq().toastmessage('showErrorToast', "The return date must be after the date of this visit");
return false;
}

//Return visit date is required if any prescription is made
if(prescription=="true" && returnDate=="" && transferOut !== 90003) {
jq().toastmessage('showErrorToast', "The return visit date is required");
return false;
}

//In case of a transfer out
if (transferOut == 90003) {
if (getValue('transfer_out_date.value') == "") {
jq().toastmessage('showErrorToast', "The transfer out date is required");
return false;
}

if (getValue('transfer_out_location.value') == "") {
jq().toastmessage('showErrorToast', "Transfer out place/facility is required");
return false;
}
}

//Return visit date is required if there is no transfer out in non POC mode
if (transferOut !== 90003 && returnDatePocClassLength <= 0  && (returnDate=="" || returnDate==null )) {
jq().toastmessage('showErrorToast', "The return visit date is required");
return false;
}

if (getValue('transfer_out_date.value') !== "" && encounterDate.getTime() !== transferOutDate.getTime()) {
getField('transfer_out_date.error').html('The Transfer out date has to be exactly the same as the encounter date' +
formatDateForDisplay(encounterDate)).show();
jq().toastmessage('showErrorToast', "The Transfer out date has to be exactly the same as the encounter date");
return false;
}


if (isPatientTransferedOut(latestTransferInEncounter, latestTransferOutEncounter) && (new Date(encounterDate)) > (new Date(latestTransferOutEncounter.encounterDatetime))) {
jq().toastmessage('showErrorToast', "This Patient is a " + latestTransferOutEncounter.display + " The Visit Date Must be before the transfer out date");
return false;
}


if (getValue('returndate.value') !== "" && isPatientTransferedOut(latestTransferInEncounter, latestTransferOutEncounter) && appointment >= (new Date(latestTransferOutEncounter.encounterDatetime))) {
getField('returndate.error').html('The return date must be before the transfer out date of ' + formatDateForDisplay(transfer_out_date_id)).show();
jq().toastmessage('showErrorToast', "The return date must be before the transfer out date of " + (new Date(latestTransferOutEncounter.encounterDatetime)));
return false;
}

var viral_load_qualitative = getValue('viral-load-results-qualitative.value');
var date_viral_load_taken = getValue('date-viral-load-taken.value');
/* The date that the viral load was taken is required if a qualitative result is selected */
if (viral_load_qualitative != "") {
if (date_viral_load_taken == "") {
getField('date-viral-load-taken.error').html('Please enter the date when the viral load sample was taken').show();
jq().toastmessage('showErrorToast', 'Please enter the date when the viral load sample was taken');
return false;
}
}

/** The date the viral load was taken cannot be after the encounter date */
if (date_viral_load_taken != "") {
if (encounterDate < date_viral_load_taken) {
getField('date-viral-load-taken.error').html('The date the viral load sample was taken cannot be after the date of this visit ' + encounterDate).show();
jq().toastmessage('showErrorToast', "The date the viral load sample was taken cannot be after the date of this visit");
return false;
}
}

/* If the viral load is detected, the number of copies must be greater than 20 which is the lowest detectable level for a machine */
if ((viral_load_qualitative == 1301) && (getValue('viral-load-results-copies.value') < 20)) {
getField('viral-load-results-copies.error').html('Please enter the number of virus copies per ml. the minimum number of copies is 20').show();
jq().toastmessage('showErrorToast', "Please enter the number of virus copies per ml. the minimum number of copies is 20");
return false;
}

/* if DSDM is not FBIM or FTR or Empty and patient is not stable should not save.*/
if (jq("#patient-categorization").find("select").find(":selected").val() !== "90003" && (jq("#165143").find("select").find(":selected").val() !== "165138" && jq("#165143").find("select").find(":selected").val() !== "165140" && jq("#165143").find("select").find(":selected").val() !== "")) {
getField('165143.error').html('Patient Not Stable due to ' + instabilityCause + '. \n Only Stable Patients can be enrolled in ' + jq("#165143").find("select").find(":selected").text()).show();

jq().toastmessage('showErrorToast', " Patient Not Stable " + instabilityCause + ". \n Only Stable Patients can be enrolled in " + jq("#165143").find("select").find(":selected").text());
return false;
}



var selectedDate = getValue('returndate.value');

var formattedDate = changeFieldDateToJavascriptDate(selectedDate);


    var appointmentCountURL = window.location.origin + '/' + OPENMRS_CONTEXT_PATH +
    '/ws/rest/v1/ugandaemr/appointmentcount?nextAppointmentDate=' + formattedDate.replace(/['"]+/g, '')
    + '&encounterType=8d5b2be0-c2cc-11de-8d13-0010c6dffd0f';
    var maxNumberOfBooked = jq("#numberOfPatients").html();

    // check when a return visit date has been specified
    if(getValue('returndate.value')!== "") {
    isValid = true;
    jq.ajax({
    type: 'GET',
    dataType: 'json',
    url: appointmentCountURL,
    async: false,
    success: function (response) {
    if (response.appointmentcount >= maxNumberOfBooked) {
    getField('returndate.error').html('The patient cannot be scheduled on ' + formattedDate + ' because the limit of ' + maxNumberOfBooked + ' has been reached').show();
    jq().toastmessage('showErrorToast', 'The patient cannot be scheduled on ' + formattedDate + ' because the limit of ' + maxNumberOfBooked + ' has been reached');
    isValid = false;
    }
    },
    error: function (jqXHR, textStatus, errorThrown) {
    getField('returndate.error').html('Unable to determine the number of patients scheduled on ' + formattedDate + ' due to ' + textStatus).show();
    jq().toastmessage('showErrorToast', 'Unable to determine the number of patients scheduled on ' + formattedDate + ' due to ' + textStatus);
    isValid = false;
    }
    });
    return isValid;
    }



    isValid = true;
    if(getValue('returndate.value')!== "") {

    var publicHolidayURL = window.location.origin + '/' + OPENMRS_CONTEXT_PATH + '/ws/rest/v1/ugandaemr/publicHoliday?date=' + formattedDate ;

    jq.ajax({
        type: 'GET',
        url: publicHolidayURL,
        async:false,
        success: function(response){
            if(response.results.length){
                getField('returndate.error').html('The patient cannot be scheduled on ' + formattedDate + ' which is a public holiday, ' + response.results[0].name).show();
                jq().toastmessage('showErrorToast', 'The patient cannot be scheduled on ' + formattedDate + ' which is a public holiday, ' + response.results[0].name);
                isValid = false;
            }
        },
        error: function(jqXHR, textStatus, errorThrown) {
            getField('returndate.error').html('Unable to determine if ' + formattedDate + ' is a public Holiday').show();
            jq().toastmessage('showErrorToast', 'Unable to determine if ' + formattedDate + ' is a public Holiday');
            isValid  = false;
        }
    });

    // check the appointment dates on public holiday
    return isValid;
    }



    if (!jq("#split-regimen").find(':checkbox').prop('checked') && getValue('art-regimen.value') != "" && getValue('no-of-art-pills.value') == "") {
        getField('no-of-art-pills.error').html('No of ' + jq("#art-regimen").find(":selected").text() + ' pills required').show();
        return false;
    }

    if (getValue('isoniazid-trigger.value') != "" && getValue('isoniazid-pills.value') == "") {
        getField('isoniazid-pills.error').html('No of isoniazid pills required').show();
        return false;
    }

    if (getValue('vitamin-b6-trigger.value') != "" && getValue('vitamin-b6-pills.value') == "") {
        getField('vitamin-b6-pills.error').html('No. of Vitamin B6 pills required').show();
        return false;
    }


    if (getValue('99413-target.value') != "" && getValue('160856.value') == "") {
        getField('160856.error').html('No. of Dapsone/Cotrimoxazole pills  required').show();
        return false;
    }

    if (getValue('fluconazole-trigger.value') != "" && getValue('fluconazole-pills.value') == "") {
        getField('fluconazole-pills.error').html('No. of fluconazole pills required').show();
        return false;
    }

    if ((getValue('other-meds-trigger.value') != ""  && getValue('other-meds-trigger.value') != null) && getValue('other-meds-quantity.value') == "") {
        getField('other-meds-quantity.error').html('Quantity of ' + getValue('other-meds-trigger.value') + ' required').show();
        return false;
    }



    if (getValue('order-viral-load-test.value') === "165412" &&  getValue('viral-load-accession-number.value') === ""){
    getField('viral-load-accession-number.error').html('The Specimen ID/Serial No field can not be empty when ordering for Viral load').show();
    jq().toastmessage('showErrorToast', "The Specimen ID/Serial No field can not be empty when ordering for Viral load");
    return false;
    }

    if (getValue('order-viral-load-test.value') === "165412" &&  getValue('viral-load-specimen-source.value') === ""){
        getField('viral-load-specimen-source.error').html('Select a specimen source when ordering for Viral load').show();
        jq().toastmessage('showErrorToast', "Select a specimen source when ordering for Viral load");
        return false;
    }



    var cohort = getValue('refill_point_id.value');
    var date = encounterDate.getFullYear()+'-'+(encounterDate.getMonth()+1)+'-'+encounterDate.getDate();
    var DSDCategory = getValue('cddp_category.value');
    if (cohort == ""){
        if(DSDCategory==="a6a0ab45-b7dc-43b2-b28c-45a91f968ebf" || DSDCategory==="d77cc565-5e53-4633-8fc7-956cfc1831d9"){
            jq().toastmessage('showErrorToast', "Select at least a DSD Refill for this client");
            return false;
        }
    }else{
        var dataToPost = "{\"patient\": \"" +  patientUUID + "\", \"cohort\": \"" + cohort + "\",\"startDate\": \"" + date+ "\"}";
        jq.ajax({
        type: "POST",
        url: '/' + OPENMRS_CONTEXT_PATH + "/ws/rest/v1/cohortm/cohortmember/",
        dataType: "json",
        contentType: "application/json",
        accept: "application/json",
        data: dataToPost,
        success: function (data) {
            jq().toastmessage('showSuccessToast', "Client Added to a Refill Group Successfully");
        },
        error: function (response) {
            if (cohort !== ""){
                var errorMessage = response.responseJSON.error.message;
                if(errorMessage.indexOf("already exists") > 0){
                    jq().toastmessage('showSuccessToast', response.responseJSON.error.message);
                } else {
                    jq().toastmessage('showErrorToast', response.responseJSON.error.message);
                }
            }
        }
    });
    }


return true;
});

}




    var calculateBmi = function (wt, ht) {
        var bmi = null
        if (wt && ht) {
            bmi = wt / ((ht / 100) * (ht / 100));
        }
        return bmi;
    }



        //functions to handle updating the bmi when in ENTER mode
        var updateBmi = function () {
            var wt = htmlForm.getValueIfLegal('weight.value');
            var ht = htmlForm.getValueIfLegal('height.value');

            var bmi = calculateBmi(wt, ht);

            if (bmi == null || isNaN(bmi)) {
                jq('#calculated-bmi-wrapper').hide();
            } else {
                jq('#no-calculated-bmi').hide();
                jq('#calculated-bmi-wrapper').show();
                jq('#calculated-bmi').html(bmi.toFixed(1));
                jq('#hidden-calculated-bmi').val(bmi.toFixed(1));
            }
        };


    // set the blood pressure fields as "expected"
    jq('#bp_systolic > input').addClass('expected');
    jq('#bp_diastolic > input').addClass('expected');


        // handle displaying the bmi when in VIEW mode
        // we have to iterate through in case there are multiple vitals forms
        // displayed on a single page

        jq('htmlform').each(function (index, form) {
        jq(form).find('#calculated-bmi-continue').hide();
        jq(form).find('#no-calculated-bmi').hide();

        var wt = jq(form).find('#weight').find('.value').text();
        var ht = jq(form).find('#height').find('.value').text();

        var bmi = calculateBmi(wt, ht);

        if (bmi != null && !isNaN(bmi)) {
            jq(form).find('#calculated-bmi-wrapper').show();
            jq(form).find('#calculated-bmi').html(bmi.toFixed(1));
            jq(form).find('#hidden-calculated-bmi').val(bmi.toFixed(1));
            }
        });

restrictByRole.script:
jq(function () {
    jq().toastmessage('showErrorToast', "Your account has a role Organizational: System Administrator which is prohibited from data entry");
    alert('Your account has a role Organizational: System Administrator which is prohibited from data entry');

    var queryParameters = {}, queryString = location.search.substring(1),
        re = /([^&=]+)=([^&]*)/g, m;
    // Creates a map with the query string parameters
    while (m = re.exec(queryString)) {
        queryParameters[decodeURIComponent(m[1])] = decodeURIComponent(m[2]);
    }
    ''
    location.href = window.location.protocol + "//" + window.location.host +
        '/' + OPENMRS_CONTEXT_PATH + '/coreapps/clinicianfacing/patient.page?patientId='
        + queryParameters['patientId']; // Causes page to reload
});
