htmlform.script:
        if (jQuery) {

            jq(document).ready(function () {

                //Block Multiple encounter entry on same date
                blockEncounterOnSameDateEncounter(jq("#encounterDate").find("input[type=hidden]"),"block");

                var motherAge  = ;

                //determine muac color code for mother
                getMUACCodeFromMUACScoreByAge(motherAge, "mother-muac-score", "mother-muac-code");

                //determine muac color code for infant
                getMUACCodeFromMUACScoreByAge("", "infant-muac-score", "infant-muac-code");

                //disable necessary fields on form load
                disable_fields("tfv-woman");
                disable_fields("tfv-partner")
                disable_fields("pre-art-no-woman");
                disable_fields("pre-art-no-partner");
                disable_fields("routine-administration");
                disable_fields("who-clinical-stage");
                disable_fields("cd4-count");
                disable_fields("cd4-date");
                disable_fields("viral-load-date");
                disable_fields("viral-load-results");
                disable_fields("viral-load-copies");
                disable_fields("other-facility-referred-from");
                disable_fields("other-facility-referred-to");

                //enable necessary fields if woman is HIV positive
                jq("#emtct-code-woman").change(function () {
                    var emtctValueWoman = getValue("emtct-code-woman.value");
                    if(emtctValueWoman == "99315" || emtctValueWoman == 99736 || emtctValueWoman == "99741") {
                        enable_fields("tfv-woman");
                        enable_fields("routine-administration");
                        enable_fields("pre-art-no-woman");
                        enable_fields("who-clinical-stage");
                        enable_fields("cd4-count");
                        enable_fields("cd4-date");
                        enable_fields("viral-load-date");
                        enable_fields("viral-load-results");
                        enable_fields("viral-load-copies");
                    } else {
                        disable_fields("tfv-woman");
                        disable_fields("routine-administration");
                        disable_fields("pre-art-no-woman");
                        disable_fields("who-clinical-stage");
                        disable_fields("cd4-count");
                        disable_fields("cd4-date");
                        disable_fields("viral-load-date");
                        disable_fields("viral-load-results");
                        disable_fields("viral-load-copies");
                    }
                });

                //enable necessary fields if partner is HIV positive
                jq("#emtct-code-partner").change(function () {
                    var emtctValuePartner = getValue("emtct-code-partner.value");
                    if(emtctValuePartner == "99315" || emtctValuePartner == 99736 || emtctValuePartner == "99741") {
                        enable_fields("tfv-partner");
                        enable_fields("pre-art-no-partner");
                    } else {
                        disable_fields("tfv-partner");
                        disable_fields("pre-art-no-partner");
                    }
                });

                //enable pre-art number field if woman is on art
                jq("#art-code-woman").change(function () {
                    if(getValue("art-code-woman.value") == "1175") {
                        disable_fields("pre-art-no-woman");
                    } else {
                        enable_fields("pre-art-no-woman");
                    }
                });

                //enable pre-art number field if partner is on art
                jq("#art-code-partner").change(function () {
                    if(getValue("art-code-partner.value") == "1175") {
                        disable_fields("pre-art-no-partner");
                    } else {
                        enable_fields("pre-art-no-partner");
                    }
                });

                //enable other facility referred from
                jq("#facility-referred-from").change(function () {
                    if (jq(this).find("select").find(":selected").val() == "90002") {
                        enable_fields("other-facility-referred-from");
                    } else {
                        disable_fields("other-facility-referred-from");
                    }
                });

                //enable other facility referred to
                jq("#facility-referred-to").change(function () {
                    if (jq(this).find("select").find(":selected").val() == "90002") {
                        enable_fields("other-facility-referred-to");
                    } else {
                        disable_fields("other-facility-referred-to");
                    }
                });

                //enable viral load copies field
                jq("#viral-load-results").change(function () {
                    var viral_load_status = getValue('viral-load-results.value');
                    if (viral_load_status == "") {
                        jq("#viral-load-status").hide();
                    }
                    if (viral_load_status == 1301) {
                        /* Detected */

                            setValue('viral-load-copies.value', '');


                        var viral_load_copies = getValue('viral-load-copies.value');
                        if(viral_load_copies == 1) {
                            jq("#viral-load-status").hide();
                            setValue('viral-load-copies.value', '');
                        }
                    }
                    if (viral_load_status == 1306) {
                        /* Not Detected */
                        jq("#viral-load-status").hide();
                        setValue('viral-load-results.value', 1);
                    }
                });
                //trigger a change when the form loads
                jq("#viral-load-results").change();

                //determine viral load status
                jq("#viral-load-copies").change(function () {
                    jq("#viral-load-status").show();
                    var vl_copies = jq(this).find("input").val();
                    if (vl_copies ==" " || vl_copies == 0) {
                        jq("#viral-load-status").hide();
                    }
                    if (vl_copies < 1000) {
                        jq("#viral-load-status").html("Suppressed");
                    } else if (vl_copies >= 1000) {
                        jq("#viral-load-status").html("Non-suppressed");
                    } else {
                        jq("#viral-load-status").hide();
                    }
                });
            });

        jq(document).ready(function () {
            $allRows = jq(this).find('.multi-drug');
            $allRows.hide();
            jq('#1-removeEntry').remove();
            jq('#10-addEntry').remove();
            jq('#1-toggleContainer').show();

            jq('#11-removeEntry').remove();
            jq('#20-addEntry').remove();
            jq('#11-toggleContainer').show();
        });

        jq(document).ready(function () {
            jq('.addEntry').on("click", function () {
                var correctedAddButtonId = parseFloat(this.id) + 1;
                var contentAddId = "#" + correctedAddButtonId + "-toggleContainer";
                jq(contentAddId).toggle(true);
                jq('#' + this.id).toggle(false);
                jq('#' + parseFloat(this.id) + '-removeEntry').toggle(true);
                return false;
            });
        });

        jq(document).ready(function () {
            jq('.removeEntry').on("click", function () {
                var correctedRemoveButtonId = parseFloat(this.id) - 1;
                var contentAddId = "#" + parseFloat(this.id) + "-toggleContainer";
                jq(contentAddId).toggle(false);
                jq(':input:not(:button)', contentAddId).val([]);
                jq('#' + correctedRemoveButtonId + '-addEntry').toggle(true);
                jq('#' + correctedRemoveButtonId + '-removeEntry').toggle(true);

                return false;
            });

        });


            jq(document).ready(function () {
                $allRows = jq(this).find('.multi-drug');
                $allRows.hide();
                jq('#1-removeEntry').remove();
                jq('#10-addEntry').remove();
                jq('#1-toggleContainer').show();
                jq('#11-removeEntry').remove();
                jq('#20-addEntry').remove();
                jq('#11-toggleContainer').show();
                for (let x = 0; x<= 10; x++) {
                    if (jq(this).find('#' +x+ '-drug').find("input[type$='text']").val()!='')
                    {
                        jq(this).find('#' +x+ '-toggleContainer').show();
                        jq(this).find('#' +x+ '-addEntry').hide();
                    }
                    else {
                        x-=1;
                        jq(this).find('#' +x+ '-addEntry').show();
                        break;
                    }
                }
            });

}
